    <script>
        $(document).ready(function () {
            $('#pmb-nav li a:not(:first)').addClass('inactive');
            $('.container').hide();
            $('.container:first').show();
            $('#pmb-nav li a').click(function () {
                var t = $(this).attr('id');
                if ($(this).hasClass('inactive')) {
                    $('#pmb-nav li a').addClass('inactive');
                    $(this).removeClass('inactive');
                    $('.container').hide();
                    $('#' + t + 'C').fadeIn('slow');
                }
                return false;
            });
        });
    </script>
